--Provide a single select statement that displays the information in the table showing who reports to whom
SELECT
	person.EMPLOYEE_NAME, 
	t.TEAM_NAME,
	p.POSITION_NAME,
	r.employee_name AS MANAGER_NAME
FROM
	NBA_PERSONNEL person
	INNER JOIN TEAM t on person.TEAM_ID = t.TEAM_ID
	INNER JOIN POSITIONS p ON person.position_id = p.position_id
	LEFT JOIN NBA_PERSONNEL r ON person.reports_to = r.employee_id;
